<?phpglobal $post;get_header();// Add template names to this array to be searched for$templates = ['front-page', $post->post_name];if(have_posts())       while (have_posts()) : the_post();         /**         * The following function should search for partials in the following order:         * 'front-page slug' -> 'page slug' -> 'template name' -> 'template' -> Finally load content.php if no other template is present.         *          * Get template parts is a handy function to use in conjunction with get_partial as it provides a list of names that can be used to determine the correct         * partial to include. Alternatively get_template_slug can be used if only the slug is required when locating partials.         */        get_partial ( 'content', array_merge( $templates, get_template_parts() ) );            endwhile;    get_sidebar();get_footer();