<?phpnamespace Monk\Forms;function gform_field_css_class($classes, $field, $form){    return $classes . ' form-group';    }add_filter('gform_field_css_class', __NAMESPACE__ . '\gform_field_css_class', 10, 3);function gform_field_container($container, $field, $form){            return str_replace(['<li', '</li>'], ['<div', '</div>'], $container);    }add_filter('gform_field_container', __NAMESPACE__ . '\gform_field_container', 10, 3);function gform_field_content($content, $field, $form){    return $content;    }add_filter('gform_field_content', __NAMESPACE__ . '\gform_field_content', 10, 3);function gform_get_form_filter($form_string, $form){        return preg_replace(["|(<div\s+class='gform_body'>)\s*<ul\s+id='gform_fields_.*?>|", "|</ul>(</div>\s*<div\s*class='gform_footer)|"], '$1', $form_string);    }add_filter('gform_get_form_filter', __NAMESPACE__ . '\gform_get_form_filter', 10, 2);/** CF7 add_filter('wpcf7_load_css', '__return_false'); // Prevent loading of default cssif(current_theme_supports('bootstrap-contact-form-7')){    function form_classes($class){        return 'form-horizontal';    }    //add_filter('wpcf7_form_class_attr', __NAMESPACE__ . '\\form_classes');    function form_tag($tag, $exec){        switch ($tag['basetype']) {            case 'submit':                $classes = array_diff(['class:btn', 'class:btn-primary'], $tag['options']);                $tag['options'] = array_merge($tag['options'], $classes);                break;            default:                if(!in_array('class:form-control', $tag['options']))                    $tag['options'][] = 'class:form-control';                break;        }        return $tag;    }    add_filter('wpcf7_form_tag', __NAMESPACE__ . '\\form_tag', 10, 2);    function form_elements($elements){        $elements = str_replace('</p>', '</div>', str_replace('<p>', '<div class="form-group" >', $elements));        return $ele