<?phpnamespace Monk\Setup;global $content_width;define("MONK_SETUP", true);$theme = wp_get_theme();/** SET DEFAULT THEME CONSTANTS **/defined("ON_MONK")    || define("ON_MONK", isset($_SERVER['ON_MONK']) && $_SERVER['ON_MONK'] === '1' ? true : false);defined('WP_ENV')    // Fallback if WP_ENV isn't defined in your WordPress config    || define('WP_ENV', 'production');define('IS_DEV', WP_ENV === 'development' ? true : false);defined('MONK_LANG')    || define('MONK_LANG', $theme->TextDomain ?: 'monk');/** Define Default Theme Supports **/// Force menu admin screenadd_theme_support( 'menus' );// This theme supports WooTheme's Sensei pluginadd_theme_support( 'sensei' );// Declare WooCommerce Supportadd_theme_support( 'woocommerce' );/* * Switch default core markup for search form, comment form, and comments * to output valid HTML5. */add_theme_support( 'html5', array(    'search-form',    'comment-form',    'comment-list',    'gallery',    'caption') );/** * Set the default content width based on the theme's design and stylesheet. * Default to Bootstrap's Large devices breakpoint (large desktops, 1200px and up)  */isset( $content_width )    || $content_width = 1140; //is_admin() Bug when regenerating images!//    && $content_width = 600;/** * @todo Document me. */isset ( $monk_default_media_ratio )    || $GLOBALS['monk_default_media_ratio'] = 9/16; // Standard widescreen ratio./** * @todo Document me. */isset ( $monk_theme )    || $GLOBALS['monk_theme'] = $theme;function setup(){        global $monk_default_media_ratio, $content_width, $_wp_additional_image_sizes;	/*	 * Make theme available for translation.	 * Translations can be filed in the /languages/ directory.	 */        is_textdomain_loaded(MONK_LANG)            || load_theme_textdomain( MONK_LANG, get_template_directory() . '/languages' );	/*	 * Define the default thumbnail size for images if not already defined in monk_setup()	 */        $width = max([300, round($content_width/3)]);	current_theme_supports("post-thumbnails") && !has_image_size('post-thumbnail')            && set_post_thumbnail_size($width, round($width * $monk_default_media_ratio), true);       	/**         * Define the default navigation menu if not already set in monk_setup() and if support is declared for this theme.         *          */        current_theme_supports('monk-default-nav-menu') && is_nav_menu('primary')            || register_nav_menu("primary", "Main Menu");        	/*         * @todo add support for this         * 	 * This theme styles the visual editor to resemble the theme style,	 * specifically font, colors, icons, and column width.	 */	//add_editor_style( );}add_action('after_setup_theme', __NAMESPACE__ . "\\setup", 15); // Should fire after monk_setup() in functions.php/** Register sidebar/s **/function register_sidebar($register = false, $base = [], $number = 1){    $base_widget = [        'name'          => __( 'Sidebar', MONK_LANG ),        'id'            => 'sidebar',        'description'   => __( 'Add widgets here to appear in your sidebar.', MONK_LANG ),        'before_widget' => '<aside id="%1$s" class="widget %2$s">',        'after_widget'  => '</aside>',        'before_title'  => '<h3 class="widget-title">',        'after_title'   => '</h3>',    ];    /**     * Register the default Sidebar/s     */    \current_theme_supports('monk-sidebar-widgets')         && \register_sidebars((int)get_theme_support('monk-sidebar-widgets') ?: 1, $base_widget);       /**     * Register footer sidebars (if any)     */    \current_theme_supports('monk-footer-widgets')        && \register_sidebars(get_theme_support('monk-footer-widgets') ?: 1, [        'name'          => __('Footer ', MONK_LANG),        'id'            => "footer",        'description'   => __('Widgets here will appear at the bottom just above the footer.', MONK_LANG)    ] + $base_widget); }add_action('widgets_init', __NAMESPACE__ . '\\register_sidebar');