<?php$max = apply_filters('ep/sidebars/footer/max_cols', 1);$active = array();for($i = 1; $i <= $max; $i++){	$sidebar = "footer-$i";	if(is_active_sidebar( $sidebar ))		$active[] = $sidebar;}if( !empty($active) ) :$total  = count($active);$cols   = ceil(12 / $total);$map    = array_fill(0, $total, ($total > 1 ? "column-$cols" : "column-single") . " col-sm-$cols");?><div id="footer-widget-area" class=" banner banner-grey widget-area widget-area-footer" role="complementary">    <div class="row" >        <?php foreach($active as $key => $id) : ?>        <div class="<?php echo $map[$key] ?>" >            <?php dynamic_sidebar( $id ) ?>        </div>        <?php endforeach; ?>     </div></div><?php endif;